import numpy as np

class Solution:
    def minimumTime(self, nums1: List[int], nums2: List[int], x: int) -> int:
        arr_sum = np.sum(nums1)
        time = 0

        while arr_sum > x:
            max_index = np.argmax(nums1)
            if nums1[max_index] == 0:
                return -1
            arr_sum -= nums1[max_index]
            nums1[max_index] += nums2[max_index]
            time += 1

        return time

