class Solution:
    def countSteppingNumbers(self, low: str, high: str) -> int:
        MOD = 10**9+7
        def isStepping(num):
            num = str(num)
            return all(abs(int(num[i+1]) - int(num[i])) == 1 for i in range(len(num) - 1))
        def dp(n, last):
            if n == 0: 
                return 1
            count = 0
            if last < 9:
                count += dp(n - 1, last + 1)
                count %= MOD
            if last > 0:
                count += dp(n - 1, last - 1)
                count %= MOD
            return count

        count = sum(dp(len(low), i) for i in range(1, 10)) - sum(isStepping(i) for i in range(1, int(low)))
        for n in range(len(low) + 1, len(high) + 1):
            count += sum(dp(n, i) for i in range(10))
        count += sum(isStepping(i) for i in range(int(high[-1]) + 1)) if len(high) > len(low) else isStepping(int(high))
        count %= MOD
        return int(count)

