from typing import List

class Solution:
    def countPalindromePaths(self, parent: List[int], s: str) -> int:
        graph = [[] for _ in range(len(parent))]
        for child, par in enumerate(parent):
            graph[par].append(child)
        count = [0] * 26
        result = [0]
        visited = [0] * len(parent)

        def dfs(node):
            count[ord(s[node]) - ord('a')] += 1

            if visited[node] == 0:
                visited[node] = 1

                odd = sum([1 for i in count if i % 2 == 1])
                if odd <= 1:
                    result[0] += 1

                for nei in graph[node]:
                    if visited[nei] == 0:
                        dfs(nei)

            count[ord(s[node]) - 97] -= 1

        dfs(0)
        return result[0] - 1

