import heapq
from collections import defaultdict
from typing import List

class Solution:
    def findMaximumElegance(self, items: List[List[int]], k: int) -> int:
        by_category = defaultdict(list)
        for profit, category in items:
            by_category[category].append(profit)
        data = []
        for category, profits in by_category.items():
            profits.sort(reverse=True)  
            data.append((-profits[0], 1, profits, category))  
        heapq.heapify(data)
        total_profit = number_of_distinct_categories = 0
        for _ in range(k):
            if not data or k - _ > number_of_distinct_categories + len(data) - 1:
                profit, index, profits, category = heapq.heappop(data)
                total_profit -= profit
                number_of_distinct_categories += 1
                if index < len(profits):  
                    heapq.heappush(data, (-profits[index], index + 1, profits, category))
            else:  
                profit, index, profits, category = heapq.heappop(data)
                total_profit -= profit
                if index < len(profits):
                    heapq.heappush(data, (-profits[index], index + 1, profits, category))
        return total_profit + number_of_distinct_categories ** 2

