class Solution:
    def survivedRobotsHealths(self, positions, healths, directions):
        import heapq

        heap = sorted([(p, -h if d == 'R' else h) for p, h, d in zip(positions, healths, directions)])
        next_heap, going_left = [], []

        while heap:
            pos, health = heap.pop(0)
            if health < 0:  # moving right
                while going_left and going_left[-1][0] <= -health:  # collisions
                    if going_left[-1][0] < -health:
                        health += 1  # the robot moving right survives
                    going_left.pop()  # remove the collided robot
                if going_left and going_left[-1][0] == -health:
                    going_left.pop()  # both robots are removed
            else:  # moving left
                going_left.append((health, pos))  # add to waiting station
            if health < 0:  # still moving right
                next_heap.append((pos, health))

        next_heap.extend((pos, -health) for health, pos in going_left)

        if heap != next_heap:
            heap = next_heap
            next_heap, going_left = [], []

        return sorted(heap, key=lambda x: positions.index(x[0]))

