import numpy as np
import heapq

INF = int(1e9)

class Solution:
    def minReverseOperations(self, n: int, p: int, banned: List[int], k: int) -> List[int]:
        banned = set(banned)
        costs = [INF]*n
        costs[p] = 0
        queue = [(0, p)]
        while queue:
            cur_cost, cur = heapq.heappop(queue)
            if cur_cost != costs[cur]:
                continue
            for direction in [-1, 1]:
                neighbor = cur + direction * (k - 1)
                if 0 <= neighbor < n and neighbor not in banned and cur_cost + 1 < costs[neighbor]:
                    costs[neighbor] = cur_cost + 1
                    heapq.heappush(queue, (cur_cost + 1, neighbor))
        return [cost if cost != INF else -1 for cost in costs]

