from collections import Counter

class Solution:
    def minCost(self, basket1: list[int], basket2: list[int]) -> int:
        counter = Counter(basket1 + basket2)
        mismatch = [cost for cost, freq in counter.items() if freq % 2 == 1]

        if len(mismatch) % 2: return -1

        mismatch.sort()
        total = sum(mismatch[:len(mismatch) // 2])

        if mismatch and total - mismatch[0] + min(counter) < total:
            return total - mismatch[0] + min(cost for cost, freq in counter.items() if freq > 1)

        return total

