from typing import List
from collections import deque

class Solution:
    def shortestCycle(self, n: int, edges: List[List[int]]) -> int:
        adj = [[] for _ in range(n)]
        shortest_cycle = n + 1

        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        for i in range(n):
            visited = [-1 for _ in range(n)]
            visited[i]=0
            queue = deque([i])

            while queue:
                node = queue[0]
                queue.popleft()

                for neighbor in adj[node]:
                    if visited[neighbor] == -1:
                        visited[neighbor] = visited[node] + 1
                        queue.append(neighbor)
                    elif visited[neighbor] >= visited[node]:
                        shortest_cycle = min(shortest_cycle, visited[node] + visited[neighbor] + 1) 

        if shortest_cycle == n + 1:
            return -1

        return shortest_cycle

