        from math import gcd
        from numpy import prod
        left_product = 1
        right_product = prod(nums)
        for i in range(len(nums) - 1):
            left_product *= nums[i]
            right_product //= nums[i]
            if gcd(left_product, right_product) == 1:
                return i
        return -1

