from typing import List

class Solution:
    def findCrossingTime(self, n: int, k: int, time: List[List[int]]) -> int:
        import heapq

        time = sorted(enumerate(time), key=lambda x: (sum(x[1][:3]), x[0]))
        workers_heap = []
        position = [0] * k

        for _ in range(n):
            if not workers_heap:
                worker_idx, t = time.pop()
                heapq.heappush(workers_heap, (t[0] + t[1] + t[2], worker_idx))
                position[worker_idx] = t[0] + t[1] + t[2] + t[3]
            else:
                next_crossing, worker_idx = heapq.heappop(workers_heap)
                if time and time[-1][1][0] + position[time[-1][0]] <= next_crossing:
                    _, t = time.pop()
                    heapq.heappush(workers_heap, (t[0] + position[_] + t[1] + t[2], _))
                    position[_] += t[0] + t[1] + t[2] + t[3]
                else:
                    heapq.heappush(workers_heap, (next_crossing + time[worker_idx][1][1] + time[worker_idx][1][2], worker_idx))
                    position[worker_idx] = next_crossing + time[worker_idx][1][1] + time[worker_idx][1][2] + time[worker_idx][1][3]

        while len(workers_heap) > 1:
            next_crossing, worker_idx = heapq.heappop(workers_heap)
            if time and time[-1][1][0] + position[time[-1][0]] <= next_crossing:
                _, t = time.pop()
                heapq.heappush(workers_heap, (t[0] + position[_] + t[1] + t[2], _))
                position[_] += t[0] + t[1] + t[2] + t[3]
            else:
                heapq.heappush(workers_heap, (next_crossing + time[worker_idx][1][1] + time[worker_idx][1][2], worker_idx))
                position[worker_idx] = next_crossing + time[worker_idx][1][1] + time[worker_idx][1][2] + time[worker_idx][1][3]

        _, last_worker = heapq.heappop(workers_heap)
        return position[last_worker] - time[last_worker][1][3]

