class Solution:
    def countAnagrams(self, s: str) -> int:
        words = s.split()
        modulo = 10**9+7
        factorials = [1]*100001
        for i in range(1, 100001):
            factorials[i] = (factorials[i-1]*i) % modulo

        def count(word):
            counter = [0]*26
            for c in word:
                counter[ord(c)-97] += 1
            res = factorials[len(word)]
            for count in counter:
                if count > 0:
                    res //= factorials[count]
            return res

        res = 1
        for word in words:
            res = (res * count(word)) % modulo
        return res

