from typing import List

class Solution:
    def minimumTotalCost(self, nums1: List[int], nums2: List[int]) -> int:
        n = len(nums1)
        graph = [[] for _ in range(n + 1)]
        visited = [False] * (n + 1)
        indegree = [0] * (n + 1)
        outdegree = [0] * (n + 1)
        cycle = [0] * (n + 1)

        for i in range(n):
            outdegree[nums1[i]] += 1
            indegree[nums2[i]] += 1
            graph[nums1[i]].append((nums2[i], i))

        def dfs(node, mn):
            visited[node] = True
            cycle[node] = mn
            total = 0
            for nei, i in graph[node]:
                if visited[nei]:
                    if cycle[nei] != mn:
                        return -1
                else:
                    res = dfs(nei, min(mn, i))
                    if res == -1:
                        return -1
                    total += res
            return total

        total_cost = 0
        for i in range(1, n + 1):
            if not visited[i] and outdegree[i] > 0 and indegree[i] > 0:
                res = dfs(i, i)
                if res == -1:
                    return -1
                total_cost += res
        return total_cost

