    def magnificentSets(self, n: int, edges: List[List[int]]) -> int:
        graph = collections.defaultdict(list)
        seen = {}
        res = 0
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)
        def dfs(node, group):
            if node in seen:
                return seen[node] == group
            seen[node] = group
            return all(dfs(nei, group ^ 1) for nei in graph[node])
