        prefix[1] = 1
        for i in range(1, len(s)):
            dp[i] = dp[i-1].copy()
            prefix[i+1] = prefix[i]
            if i + 1 >= minLength:
                for j in range(1, min(k, (i+1)//minLength) + 1):
                    dp[i][j] += prefix[i - minLength + 1]
                    prefix[i+1] += dp[i][j]
            if s[i] in R:
                l, e = i - 1, prefix[i]
                while l >= 0 and s[l] not in R:
                    prefix[i+1] -= dp[l][j] if j >= 1 else 0
                    dp[l][j] = 0 if j >= 1 else dp[l][j]
                    l -= 1
                if l >= 0:
                    for j in range(min(k, i//minLength) + 1):
                        dp[i][j] += dp[l][j-1 if j >= 1 else 0] if j >= 1 or l == 0 else 0
                        prefix[i+1] += dp[i][j] - e
