class Solution:
    def splitMessage(self, message: str, limit: int) -> list:
        message_length = len(message)
        max_suffix = len('<a/b>') + len(str(message_length)) 
        if limit <= max_suffix:
            return []

        part_length = limit
        while part_length > 0:
            b = message_length // (part_length - max_suffix)
            a = 1
            parts = []
            suffix_length = max_suffix
            while len(message) >= part_length:
                if b >= 10**suffix_length:
                    suffix_length += 1
                if len(message) < suffix_length:
                    break
                parts.append(message[:part_length-suffix_length]+'<'+str(a)+'/'+str(b)+'>')
                message = message[part_length-suffix_length:]
                a += 1

            if len(message) == 0:
                return parts

            part_length -= 1
        return []

