from collections import defaultdict
from typing import List

class Solution:
    def componentValue(self, nums: List[int], edges: List[List[int]]) -> int:
        totalSum, nodesCount = sum(nums), len(nums)
        if totalSum % 2: return 0
        graph, visited = defaultdict(list), [0] * nodesCount
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            nonlocal result
            for child in graph[node]:
                if child != parent:
                    nums[node] += dfs(child, node)
            if nums[node] == totalSum / 2 and node != 0:
                result += 1
                return 0
            return nums[node]

        result = dfs(0, -1)
        return result if nums[0] == totalSum / 2 else 0

