class Solution:
    def numberOfPaths(self, grid: [[int]], k: int) -> int:
        m, n = len(grid), len(grid[0])
        mod = 10**9 + 7
        dp = [[[0]*k for _ in range(n)] for _ in range(2)]
        dp[0][0][grid[0][0] % k] = 1
        for i in range(m):
            for j in range(n):
                if i == j == 0:
                    continue
                cur = dp[i & 1][j]
                pre = dp[~i & 1][j] if i > 0 else cur
                for a in range(k):
                    cur[a] = (pre[a-grid[i][j] % k] + (cur[a-grid[i][j] % k] if j > 0 else 0)) % mod
        return dp[~m & 1][-1][0]

