class Solution:
    def numberOfPairs(self, nums1, nums2, diff):
        n = len(nums1)
        pairs = sorted([(nums1[i]-nums2[i], i) for i in range(n)], reverse=True)
        j = n
        pairs_count = 0
        for i in range(n-1):
            while j > 0 and pairs[i][0] - pairs[j - 1][0] > diff:
                j -= 1
            pairs_count += j if pairs[i][1] >= j else j - 1
        return pairs_count

