class Solution:
    def idealArrays(self, n: int, maxValue: int) -> int:
        MOD = 10**9 + 7
        total = [0]*(maxValue+1)
        dp = [0]*(maxValue+1)
        for i in range(1, maxValue+1):
            dp[i] = 1
            total[i] = (total[i-1]+dp[i]) % MOD 
        for i in range(2, n+1):
            total[i-1] %= MOD 
            for j in range(maxValue,0,-1):
                dp[j] = (total[i-1] - total[j-1] + MOD) % MOD
                total[i] = (total[i]+dp[j]) % MOD
        return total[n] % MOD

