from typing import List

class Solution:
    def minimumScore(self, nums: List[int], edges: List[List[int]]) -> int:
        n = len(nums)
        G = {i: [] for i in range(n)}
        for u, v in edges:
            G[u].append(v)
            G[v].append(u)

        best, best_two = [0], [float('inf')]

        def dfs(u: int, p: int, xor: int) -> None:
            xor ^= nums[u]
            best.append(xor)
            a, b = xor, 0
            for v in G[u]:
                if v == p:
                    continue
                dfs(v, u, xor)
                b = max(b, best[-1])

                if b > a:
                    a, b = b, a
            if b > 0:
                best_two.append(a - b)
        dfs(0, -1, 0)
        return min(best_two)

