from typing import List

class Solution:
    def distinctNames(self, ideas: List[str]) -> int:
        idea_set = set(ideas)
        valid_names = set()

        for i in range(len(ideas)):
            for j in range(i+1, len(ideas)):
                idea_A, idea_B = ideas[i], ideas[j]

                new_idea_A = idea_B[0] + idea_A[1:]
                new_idea_B = idea_A[0] + idea_B[1:]

                if new_idea_A not in idea_set and new_idea_B not in idea_set:
                    valid_names.add(new_idea_A + ' ' + new_idea_B)
                    valid_names.add(new_idea_B + ' ' + new_idea_A)

        return len(valid_names)
