class TextEditor:

    def __init__(self):
        self.text = ''
        self.cursor_pos = 0

    def addText(self, text: str) -> None:
        self.text = self.text[:self.cursor_pos] + text + self.text[self.cursor_pos:]
        self.cursor_pos += len(text)

    def deleteText(self, k: int) -> int:
        if k == 0 or self.cursor_pos == 0:
            return 0
        start_pos = max(0, self.cursor_pos - k)
        deleted_chars = self.cursor_pos - start_pos
        self.text = self.text[:start_pos] + self.text[self.cursor_pos:]
        self.cursor_pos = start_pos
        return deleted_chars

    def cursorLeft(self, k: int) -> str:
        self.cursor_pos = max(0, self.cursor_pos - k)
        end = min(self.cursor_pos + 10, self.cursor_pos)
        return self.text[max(0, end - 10):end]

    def cursorRight(self, k: int) -> str:
        self.cursor_pos = min(len(self.text), self.cursor_pos + k)
        end = min(self.cursor_pos + 10, self.cursor_pos)
        return self.text[max(0, end - 10):end]

