from typing import List
import heapq

class Solution:
    def minimumObstacles(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        heap = [(0, 0, 0)]  # (obstacles, x, y)
        visited = {(0, 0): 0}

        while heap:
            obstacles, x, y = heapq.heappop(heap)

            # Ignore if visited with smaller obstacles
            if (x, y) in visited and visited[(x, y)] < obstacles:
                continue

            visited[(x, y)] = obstacles

            if (x, y) == (m - 1, n - 1):  # Destination cell
                return obstacles

            # Move to neighboring cells
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy

                if 0 <= nx < m and 0 <= ny < n:
                    new_obstacles = obstacles + grid[nx][ny]
                    heapq.heappush(heap, (new_obstacles, nx, ny))

        return -1  # No path to the destination

