from typing import List

class Solution:
    def longestPath(self, parent: List[int], s: str) -> int:
        tree = {idx: [] for idx in range(len(parent))}
        for idx, value in enumerate(parent):
            if idx != 0:
                tree[value].append(idx)
        max_path_length = 0
        def dfs(node: int, path: List[int]) -> int:
            nonlocal max_path_length
            max_length = 0
            for child in tree[node]:
                if s[child] != s[node]:
                    max_length = max(max_length, dfs(child, path + [child]))
            max_path_length = max(max_path_length, max_length + 1)
            return max_length + 1
        dfs(0, [])
        return max_path_length

