class Solution:
    def largestVariance(self, s: str) -> int:
        character_counts = [0] * 26
        current_max = max_variance = 0
        for char in s:
            character_counts[ord(char) - ord('a')] += 1
            current_max = max(character_counts)
            min_seen = min(x for x in character_counts if x > 0)
            max_variance = max(current_max - min_seen, max_variance)
        return max_variance

