import bisect

class CountIntervals:

    def __init__(self):
        self.intervals = []

    def add(self, left: int, right: int) -> None:
        if not self.intervals:
            self.intervals.append([left, right])
            return

        start, end = left, right
        lo = bisect.bisect_left(self.intervals, [start])
        hi = bisect.bisect_right(self.intervals, [end])

        if lo < len(self.intervals):
            start = min(start, self.intervals[lo][0])

        if hi > 0:
            end = max(end, self.intervals[hi-1][1])

        self.intervals[lo:hi] = [[start, end]]

    def count(self) -> int:
        return sum(right-left+1 for left,right in self.intervals)

