from typing import List
import collections

class Solution:
    def maximumMinutes(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        fire = [[float('inf')] * n for _ in range(m)]
        move = [[-1, 0], [1, 0], [0, -1], [0, 1]]

        fire_queue = collections.deque()
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 1:
                    fire[i][j] = 0
                    fire_queue.append((i, j, 0))

        while fire_queue:
            x, y, t = fire_queue.popleft()
            for dx, dy in move:
                nx, ny = x + dx, y + dy
                if 0 <= nx < m and 0 <= ny < n and grid[nx][ny] == 0 and fire[nx][ny] > t + 1:
                    fire[nx][ny] = t + 1
                    fire_queue.append((nx, ny, t + 1))

        human_queue = collections.deque([(0, 0, 0)])
        human = [[-1] * n for _ in range(m)]
        human[0][0] = fire[0][0]

        while human_queue:
            x, y, t = human_queue.popleft()
            for dx, dy in move:
                nx, ny = x + dx, y + dy
                if 0 <= nx < m and 0 <= ny < n and grid[nx][ny] == 0 and fire[nx][ny] > t + 1 and human[nx][ny] < t + 1:
                    human[nx][ny] = min(human[x][y], fire[nx][ny])
                    human_queue.append((nx, ny, t + 1))

        if human[-1][-1] == float('inf'): 
            return -1 
        if human[-1][-1] == 0: 
            return 10**9 
        return human[0][0] - 1

