class Solution:
    def fullBloomFlowers(self, flowers: List[List[int]], people: List[int]) -> List[int]:
        blooming_times = sorted((time, ind, 1) for ind, (start, _) in enumerate(flowers) for time in [start])
        wilting_times = sorted((time, ind, -1) for ind, (_, end) in enumerate(flowers) for time in [end + 1])
        events = sorted(blooming_times + wilting_times)
        blooms, res = [0] * (len(people) + 1), []
        for time, ind, blooming in events:
            blooms[blooming] += 1
        for p in people:
            while events and events[0][0] <= p:
                _, ind, blooming = events.pop(0)
                blooms[blooming] -= 1
            res.append(blooms[1])
        return res

