class Solution:
    def sumScores(self, s: str) -> int:
        n = len(s)
        prefix_table = [0] * n
        prefix_sum = [0] * (n + 1)

        length = 0
        i = 1
        while i < n:
            if s[i] == s[length]:
                length += 1
                prefix_table[i] = length
                i += 1
            else:
                if length != 0:
                    length = prefix_table[length - 1]
                else:
                    prefix_table[i] = 0
                    i += 1

        for i in range(n):
            prefix_sum[prefix_table[i]] += 1

        for i in range(n, 0, -1):
            prefix_sum[i - 1] += prefix_sum[i]

        return sum(prefix_sum)

