from collections import deque
from typing import List


class Solution:
    def minimumSeconds(self, nums: List[int]) -> int:
        distinct_nums = list(set(nums))
        n = len(nums)

        distance_from_start = {num: float('inf') for num in distinct_nums}
        queue = deque(distinct_nums)
        for num in queue:
            distance_from_start[num] = 0

        while queue:
            curr = queue.popleft()
            for i in range(len(nums)):
                if nums[i] == curr:
                    for j in [i-1, i+1]:
                        j = j % n
                        if nums[j] not in distance_from_start or distance_from_start[nums[j]] > 1 + distance_from_start[curr]:
                            distance_from_start[nums[j]] = 1 + distance_from_start[curr]
                            queue.append(nums[j])

        return max(distance_from_start.values())

