class Solution:
    def canSplitArray(self, nums: List[int], m: int) -> bool:
        n = len(nums)
        nums.sort(reverse=True)
        sums = [0]*n
        sums[0] = nums[0]
        for i in range(1, n):
            sums[i] = sums[i-1] + nums[i]
        if sums[-1] < m:
            return False
        dp = [0] + [float('-inf')]*n
        for i in range(n):
            for j in range(i+1):
                if sums[i] - (dp[j] if j > 0 else 0) >= m:
                    dp[i+1] = max(dp[i+1], min(dp[j], sums[i] - m))
        return dp[-1] >= 0

