class Solution:
    def maximumSafenessFactor(self, grid: List[List[int]]) -> int:
        n = len(grid)
        dist = [[float('inf')] * n for _ in range(n)]
        for i in range(n):
            for j in range(n):
                if grid[i][j] == 1:
                    for x in range(n):
                        for y in range(n):
                            dist[x][y] = min(dist[x][y], abs(x-i)+abs(y-j))

        dp = [[-1] * n for _ in range(n)]
        dp[0][0] = dist[0][0]
        for i in range(n):
            for j in range(n):
                if i != 0:
                    dp[i][j] = max(dp[i][j], min(dp[i-1][j], dist[i][j]))
                if j != 0:
                    dp[i][j] = max(dp[i][j], min(dp[i][j-1], dist[i][j]))
        return dp[-1][-1]

