import collections

class Solution:
    def checkArray(self, nums: List[int], k: int) -> bool:
        sums = list(nums)
        d = collections.deque()
        for i in range(len(nums)):
            if d and d[0][0] <= i:
                d.popleft()
            if d:
                sums[i] -= d[0][1]
            if sums[i] < 0:
                return False
            if sums[i] % k:
                if i > len(nums) - k:
                    return False
                d.append((i + k, sums[i] // k + 1))
                sums[i] += (sums[i] // k + 1) * k
        return True

