from typing import List
import numpy as np

class Solution:
    def maxStrength(self, nums: List[int]) -> int:
        pos = [x for x in sorted(nums) if x > 0]
        neg = [x for x in sorted(nums) if x < 0]
        neg_cum = np.cumprod(neg) if neg else []

        dp = [[0, 0]] + [[neg_cum[i], neg_cum[i]] if i % 2 == 0 else [neg_cum[i - 1], neg_cum[i]] for i in range(len(neg))]
        for x in pos:
            dp = [[max(dp[i]), x * max(dp[i])] for i in range(len(dp))] + [[0, x * dp[-1][1]]]

        return max(max(pair) for pair in dp)

