        for i in range(1, len(s)+1):
            prefx[i] = prefx[i-1]^(1 if s[i-1] == '1' else 0)

        ans = []
        for first, second in queries:
            if first == second:
                ans.append([0, 0])
                continue

            length = len(bin(second^first)) - 2

            a = [-1, -1]
            for i in range(len(s)-length+1):
                if prefx[i]^prefx[i+length] == second:
                    a = [i, i+length-1]
                    break
            ans.append(a)

