class Solution:
    def distinctPrimeFactors(self, nums):
        def is_prime(n):
            if n <= 3:
                return n > 1
            if n % 2 == 0 or n % 3 == 0:
                return False
            i = 5
            while i * i <= n:
                if n % i == 0 or n % (i + 2) == 0:
                    return False
                i += 6
            return True

        def factors(n):
            factors = set()
            for i in range(2, n + 1):
                if n % i == 0:
                    factors.add(i)
            return factors

        factor_set = set()
        for num in nums:
            factor_set = factor_set.union(factors(num))
