class Solution:
    def topStudents(self, positive_feedback: List[str], negative_feedback: List[str], report: List[str], student_id: List[int], k: int) -> List[int]:
      # Initialize points for each student
      student_points = {student: 0 for student in student_id}

      for idx, report_instance in enumerate(report):
        words = report_instance.split()
        for word in words:
          if word in positive_feedback:
            student_points[student_id[idx]] += 3
          elif word in negative_feedback:
            student_points[student_id[idx]] -= 1

      # Sort students based on their points, and then their id in ascending order
      sorted_students = sorted(student_points.items(), key=lambda x: (-x[1], x[0]))

      return [student[0] for student in sorted_students[:k]]

