        if root.val == key:
            if root.left is not None:
                tmp = root.left
                while tmp.right:
                    tmp = tmp.right
                self.pre[0] = tmp.val
            if root.right is not None:
                tmp = root.right
                while tmp.left:
                    tmp = tmp.left
                self.suc[0] = tmp.val
