class Solution:
    def longestContinuousSubstring(self, s: str) -> int:
        max_length = 0
        cur_length = 1
        prev_char = s[0]
        for c in s[1:]:
            if ord(c) == ord(prev_char) + 1:
                cur_length += 1
            else:
                max_length = max(cur_length, max_length)
                cur_length = 1
            prev_char = c
        max_length = max(cur_length, max_length)
        return max_length

