    def maximumRows(self, matrix: List[List[int]], numSelect: int) -> int:
        m, n = len(matrix), len(matrix[0])

        rowMasks = [0] * m
        for i in range(m):
            for j in range(n):
                rowMasks[i] |= (matrix[i][j] << j)

        dp = [[0] * (1 << n) for _ in range(n+1)]
        for mask in range(1 << n):
            for i in range(m):
                if rowMasks[i] & mask == rowMasks[i]:
                    dp[1][mask] += 1

        for i in range(2, n+1):
            for mask in range(1 << n):
                for subset in range(mask):
                    if subset & mask == subset:
                        dp[i][mask] = max(dp[i][mask], dp[i-1][subset] + dp[1][mask^subset])

        return dp[numSelect][int('1'*n, 2)]

