from typing import List
from collections import deque

class Solution:
    def closestMeetingNode(self, edges: List[int], node1: int, node2: int) -> int:

        dist1, dist2 = {node1: 0}, {node2: 0}
        queue1, queue2 = deque([node1]), deque([node2])
        res = float('inf')
        idx = -1

        while queue1 and queue2:

            for _ in range(len(queue1)):
                node = queue1.popleft()
                if node in dist2:
                    maxDist = max(dist1[node], dist2[node])
                    if maxDist < res or (maxDist == res and node < idx):
                        res, idx = maxDist, node
                if edges[node] != -1 and edges[node] not in dist1:
                    dist1[edges[node]] = dist1[node] + 1
                    queue1.append(edges[node])
                    edges[node] = -1

            for _ in range(len(queue2)):
                node = queue2.popleft()
