class Solution:
    def countHousePlacements(self, n: int) -> int:
        MOD = 10**9 + 7 
        f = [0] * (n + 1)
        g = [0] * (n + 1)
        f[1], g[1] = 2, 1
        for i in range(2, n + 1):
            f[i] = (2 * f[i - 1] + f[i - 2]) % MOD
            g[i] = (f[i - 1] + 2 * g[i - 1]) % MOD
        return (pow(f[n], 2, MOD) + 2 * f[n] * g[n] % MOD) % MOD

