from collections import defaultdict

class Solution:
    def largestWordCount(self, messages: List[str], senders: List[str]) -> str:
        sender_words = defaultdict(int)
        max_sender = ''
        max_words = 0

        for message, sender in zip(messages, senders):
            words = message.split(' ')
            sender_words[sender] += len(words)

            if sender_words[sender] > max_words or (sender_words[sender] == max_words and sender > max_sender):
                max_words = sender_words[sender]
                max_sender = sender

        return max_sender

