class Solution:
    def maxTrailingZeros(self, grid):
        m, n = len(grid), len(grid[0])
        dp2, dp5 = [[0]*n for _ in range(m)], [[0]*n for _ in range(m)]
        for i in range(m):
            for j in range(n):
                count2 = count5 = 0
                while grid[i][j] % 2 == 0:
                    grid[i][j] //= 2
                    count2 += 1
                while grid[i][j] % 5 == 0:
                    grid[i][j] //= 5
                    count5 += 1
                if i > 0:
