from itertools import combinations
from functools import reduce
from operator import and_
from typing import List

class Solution:
    def largestCombination(self, candidates: List[int]) -> int:
        candidates.sort(reverse=True)  # Ensure combinations with the max number are tried first
        if all(x == candidates[0] for x in candidates):  # All elements are the same
            return len(candidates)
        for size in range(len(candidates), 0, -1):  # Start with the max size
            for combination in combinations(candidates, size):
                if reduce(and_, combination) > 0:
                    return size
        return 0

