from typing import List

class Solution:
    def distinctDifferenceArray(self, nums: List[int]) -> List[int]:
        n = len(nums)
        prefix = [set()] * n
        suffix = [set()] * n
        prefix[0] = {nums[0]}
        suffix[n - 1] = {nums[n - 1]}
        for i in range(1, n):
            prefix[i] = prefix[i - 1].copy()
            prefix[i].add(nums[i])
            j = n - i - 1
            suffix[j] = suffix[j + 1].copy()
            suffix[j].add(nums[j])
        return [len(prefix[i]) - len(suffix[i + 1]) if i + 1 < n else len(prefix[i]) for i in range(n)]

