from typing import List

class Solution:
    def hardestWorker(self, n: int, logs: List[List[int]]) -> int:
        time_dict = {}
        previous_time = 0
        for log in logs:
            employee, current_time = log
            task_duration = current_time - previous_time
            if employee in time_dict:
                time_dict[employee] = max(time_dict[employee], task_duration)
            else:
                time_dict[employee] = task_duration

            previous_time = current_time

        max_duration = max(time_dict.values())
        hardest_worker = min(employee for employee in time_dict if time_dict[employee] == max_duration)
        return hardest_worker

