import math

class Solution:
    def commonFactors(self, a: int, b: int) -> int:
        # Find the greatest common divisor (gcd) of a and b
        gcd = math.gcd(a, b)

        # The number of common factors can be found be finding the number of divisors of the gcd
        factors = 0
        for i in range(1, gcd + 1):
            if gcd % i == 0:
                factors += 1

        return factors

