class Solution:
    def minTimeToType(self, word: str) -> int:
        move_time = 0
        cur_char = 'a'
        for char in word:
            clockwise_dist = abs(ord(char) - ord(cur_char))
            counter_clockwise_dist = 26 - clockwise_dist
            move_time += min(clockwise_dist, counter_clockwise_dist) + 1  # +1 for type operation
            cur_char = char
        return move_time

