class Solution:
    def is_path_crossing(self, path: str) -> bool:
        positions = {(0, 0)}
        current_position = [0, 0]

        moves = {"N": [0, 1], "S": [0, -1], "E": [1, 0], "W": [-1, 0]}

        for move in path:
            current_position[0] += moves[move][0]
            current_position[1] += moves[move][1]
            if tuple(current_position) in positions:
                return True
            positions.add(tuple(current_position))

        return False

